import React, { useState } from 'react';
import '../Form.css';

const doctors = [
  { name: 'Dr. A. Sharma', specialization: 'Cardiologist' },
  { name: 'Dr. B. Verma', specialization: 'Dermatologist' },
  { name: 'Dr. C. Singh', specialization: 'General Physician' },
];

function BookAppointment() {
  const [form, setForm] = useState({
    doctor: '',
    date: '',
    time: '',
    reason: '',
  });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.doctor || !form.date || !form.time || !form.reason) {
      alert('Please fill in all fields.');
      return;
    }
    setSubmitted(true);
  };

  if (submitted) {
    return (
      <div className="login-container">
        <a className="logo" href="/">Swasthya Sewa</a>
        <h1 className="title">Appointment Booked!</h1>
        <p>Your appointment with <b>{form.doctor}</b> is scheduled for <b>{form.date}</b> at <b>{form.time}</b>.</p>
        <a href="/dashboard-patient" className="login-btn" style={{textAlign:'center'}}>Go to Dashboard</a>
      </div>
    );
  }

  return (
    <div className="login-container">
      <a className="logo" href="/">Swasthya Sewa</a>
      <h1 className="title">Book Appointment</h1>
      <form onSubmit={handleSubmit} style={{width:'100%'}}>
        <div className="form-group">
          <select name="doctor" value={form.doctor} onChange={handleChange}>
            <option value="">Select Doctor</option>
            {doctors.map((doc, i) => (
              <option key={i} value={doc.name}>{doc.name} ({doc.specialization})</option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <input type="date" name="date" value={form.date} onChange={handleChange} />
        </div>
        <div className="form-group">
          <input type="time" name="time" value={form.time} onChange={handleChange} />
        </div>
        <div className="form-group">
          <textarea name="reason" placeholder="Reason for visit" value={form.reason} onChange={handleChange} rows={3} />
        </div>
        <button className="login-btn" type="submit">Book Appointment</button>
      </form>
    </div>
  );
}

export default BookAppointment;
